import UIKit

class CityRouter: CityWireframeProtocol {
    
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = CityViewController(nibName: nil, bundle: nil)
        let interactor = CityInteractor()
        let router = CityRouter()
        let presenter = CityPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
    
    func showSearchCityView() {
        let view = SearchCityRouter.createModule()
        self.viewController?.navigationController?.pushViewController(view, animated: true)
    }
    
    func closeView() {
        self.viewController?.navigationController?.popToRootViewController(animated: true)
    }
}
